import React, {useMemo, useState} from "react";
import useDeviceList from "../hooks/useDeviceList";
import styled from "styled-components";
import Constants from "../util/Constants";
import PropTypes from "prop-types";
import {Table} from "react-bootstrap";

const StTableWrapper = styled("div")`
  display: flex;
  flex: 1;
  width: 100%;
  flex-direction: column;
  position: relative;
  background-color: white;
  box-sizing: border-box;
  min-height: 0; /* firefix GOTCHA */
  height: 100%;
  width: 100%;
  min-height: 50%;
  min-width: 100%;
  overflow: hidden;
`;

const {COLUMNS} = Constants;


const DevicesTable = (props) => {
    const {filterBy, sortBy} = props;
    let {devicesList, isLoading} = useDeviceList("ss", "dd");
    const [edits, setEdits] = useState({});
    isLoading = false;

    const data = useMemo(() => {
        let newData = devicesList;
        if (filterBy) {
            newData = [];
            devicesList.forEach(item => {
                    filterBy.forEach(filter => {
                        if (item.type === filter) {
                            newData.push(item);
                        }
                    });
                }
            );
        }
        return Object.keys(newData).map(
            (key) => edits[key] || newData[key]
        );
    }, [devicesList, edits, filterBy, sortBy]);


    const createHeader = () => {
        let header = [];
        COLUMNS.forEach(({text}) => {
            header.push(<Table.HeaderCell>{text}</Table.HeaderCell>);
        })
        header.push(<Table.HeaderCell>Selected</Table.HeaderCell>);

        return header;
    };
    const createTable = () => {
        let table = [];
        data.forEach(({system_name, type, hdd_capacity}) => {
            let children = [];
            children.push(<Table.Cell>{system_name}</Table.Cell>);
            children.push(<Table.Cell>{type}</Table.Cell>);
            children.push(<Table.Cell>{hdd_capacity}</Table.Cell>);
            children.push(<Table.Cell> dd</Table.Cell>);
            table.push(<Table.Row children={children}/>);
        });
        return table;
    };
    return (
        <StTableWrapper>
            <Table responsive>
                {COLUMNS.forEach(({ text}) => (<th>{text}</th>))}
            </Table>
        </StTableWrapper>
    );
};

DevicesTable.propTypes = {
    filterBy: PropTypes.string,
    sortBy: PropTypes.string,
}

DevicesTable.propTypes = {
    filterBy: undefined,
    sortBy: undefined,
}
export default DevicesTable;
