import DevicesTable from "../Components/DevicesTable";
import React, { useState } from "react";
import Constants from "../util/Constants";
import {Dropdown, Label} from "semantic-ui-react";

const {DEVICE_TYPE, COLUMNS, LABELS} = Constants;


const Dashboard = () => {
    const [filterBy, setFilterBy] = useState(undefined);
    const [sortBy, setSortBy] = useState(undefined);

    const handleFilter=(e, {value})=>{
        console.log("filterBy==>", value);
        setFilterBy(value);
    };
    const handleSort=(e, { value })=>{
        setSortBy(value);
        console.log("sortBy==>", sortBy);
    };

    return (<>
            <p>
                <Label>{LABELS.filter_device}</Label>
                <Dropdown placeholder='Select one'
                          fluid
                          multiple
                          search
                          selection
                          options={DEVICE_TYPE}
                          value={sortBy}
                onChange={handleFilter}/>
            </p>
            <p>
                <Label>{LABELS.sort_by}</Label>
                <Dropdown placeholder='Select one'
                          fluid
                          multiple
                          search
                          selection
                          options={COLUMNS}
                          onChange={handleSort}/>
            </p>
            <p>
                <DevicesTable
                    sortBy={sortBy}
                    filterBy={filterBy}
                />
            </p>
        </>
    )
};

export default Dashboard;
