import * as DeviceService from "../services/deviceService";
import {useCallback} from "react";
import Constants from "../util/Constants";
const {POST, PUT, GET, DELETE} = Constants;

const useDeviceForm = () => {
  const create = useCallback(async ({entity}) => {
        try {
            return await DeviceService.save({data: entity, method: POST});
        } catch (e) {
            //TODO Handle errors
            return false;
        }
    }, []);

    const update = useCallback(async ({entity}) => {
        try {
            return await DeviceService.update({data: entity, method: PUT});
        } catch (e) {
            //TODO Handle errors
            return false;
        }
    }, []);

    const remove = useCallback(async ({entity}) => {
        try {
            return await DeviceService.remove( {data: entity, method: DELETE});
        } catch (e) {
            //TODO Handle errors
            return false;
        }
    }, []);

    return {create, update, remove};
}

export default useDeviceForm;
