import DevicesTable from "../Components/DevicesTable";
import React, {useState} from "react";
import Constants from "../util/Constants";
import {Form, Button, Modal} from "react-bootstrap";
import CreateEditForm from "./CreateEditForm";

const {DEVICE_TYPE, COLUMNS, LABELS} = Constants;


const Dashboard = () => {
    const [filterBy, setFilterBy] = useState(undefined);
    const [sortBy, setSortBy] = useState(undefined);
    const [showNew, setShowNew] = useState(false);

    const handleFilter = (event) => {
        setFilterBy(event.target.value);
    };
    const handleSort = (event) => {
        setSortBy(event.target.value);
    };

    const drawOptions = (data) => {
        let options = [];
        data.forEach((option) => {
            options.push(<option key={option.value} value={option.value}>{option.text}</option>);
        })
        return options;
    };

    const handleNew = () => {
        setShowNew(true);
    }


    return (<>
            <p>
                <Form.Label>{LABELS.filter_device} </Form.Label>
                <Form.Control as="select" onChange={handleFilter}>
                    <option>All</option>
                    {drawOptions(DEVICE_TYPE)}
                </Form.Control>
            </p>
            <p>
                <Form.Label>{LABELS.sort_by} </Form.Label>
                <Form.Control as="select" onChange={handleSort}>
                    <option>All</option>
                    {drawOptions(COLUMNS)}
                </Form.Control>
            </p>
            <p>
                <Button variant="outline-primary"
                        onClick={handleNew}>
                    New
                </Button>
            </p>
            <p>
                {showNew ? <CreateEditForm/> : <></>}
            </p>
            <p>
                <DevicesTable
                    sortBy={sortBy}
                    filterBy={filterBy}
                />
            </p>
        </>
    )
};

export default Dashboard;
