import * as DeviceService from "../services/deviceService";
import {useCallback} from "react";

const useDeviceForm = () => {
    const create = useCallback(async ({entity}) => {
        try {
            const response = await DeviceService.save(entity, "devices");
            return response;
        } catch (e) {
            return false;
        }
    }, []);
    return {create};

    const update = useCallback(async ({entity}) => {
        try {
            const response = await DeviceService.save(entity, "devices");
            return response;
        } catch (e) {
            return false;
        }
    }, []);

    const remove = useCallback(async ({entity}) => {
        try {
            const response = await DeviceService.remove(entity, "devices");
            return response;
        } catch (e) {
            return false;
        }
    }, []);
    return {create};
}

export default useDeviceForm;
