import React from "react";
import {Button, Form} from "react-bootstrap";
import PropTypes from "prop-types";
import Constants from "../util/Constants";

const {DEVICE_TYPE} = Constants;

const CreateEditForm = (props) => {
    const {visible} = props;
    const drawOptions = (data) => {
        let options = [];
        data.forEach((option) => {
            options.push(<option key={option.value} value={option.value}>{option.text}</option>);
        })
        return options;
    };

    return <Form>
        <Form.Group className="mb-3" controlId="formBasicSystemName">
            <Form.Label>System name</Form.Label>
            <Form.Control type="text" placeholder="Enter system name" required/>
        </Form.Group>

        <Form.Group className="mb-3" controlId="formBasicType">
            <Form.Label>Type</Form.Label>
            <Form.Control as="select" >
                {drawOptions(DEVICE_TYPE)}
            </Form.Control>
        </Form.Group>
        <Form.Group className="mb-3" controlId="formBasicCapacity">
            <Form.Label>HDD Capacity</Form.Label>
            <Form.Control type="text" placeholder="Enter an HDD Capacity" required/>
        </Form.Group>
        <Button variant="primary" type="submit">
            Submit
        </Button>
    </Form>

}

CreateEditForm.propTypes = {
    visible: PropTypes.bool,
}
CreateEditForm.defaultProps = {
    visible: false,
}

export default CreateEditForm;
