import * as DeviceService from "../services/deviceService";
import {useCallback} from "react";

const useDeviceForm = () => {
    const create = useCallback(async ({entity}) => {
        try {
            const response = await DeviceService.save(entity, "devices");
            return response;
        } catch (e) {
            return false;
        }
    }, []);

    const update = useCallback(async ({entity}) => {
        try {
            console.log("update entity=>", entity);
            const response = await DeviceService.save(entity, "devices", entity?.id);
            return response;
        } catch (e) {
            return false;
        }
    }, []);

    const remove = useCallback(async ({entity}) => {
        try {
            console.log("entity=>", entity);
            const response = await DeviceService.remove(entity, "devices", entity?.id);
            return response;
        } catch (e) {
            return false;
        }
    }, []);

    return {create, update, remove};
}

export default useDeviceForm;
