import React, {useEffect, useState} from "react";
import {Button, Form, Modal} from "react-bootstrap";
import PropTypes from "prop-types";
import Constants from "../util/Constants";
import useDeviceForm from "../hooks/useDeviceForm";
import {Alert} from "bootstrap";

const {DEVICE_TYPE} = Constants;

const CreateEditForm = (props) => {
    const [show, setShow] = useState(true);
    const [validated, setValidated] = useState(false);
    const [saved, setSaved] = useState(false);
    const {entity} = props;
    const {create} = useDeviceForm();
    let entitySaved;

    console.log("entra a form");
    const handleSubmit = (event) => {
        const form = event.currentTarget;
        if (form.checkValidity() === false) {
            event.preventDefault();
            event.stopPropagation();
        }
        setValidated(true);
        if (!entity) {
            entitySaved = {
                system_name: form.elements['system_name'].value,
                type: form.elements['type'].value,
                hdd_capacity: form.elements['hdd_capacity'].value
            };
            create({entity: entitySaved}).then(() => setSaved(true));
        }
    };

    const successMessage = () => {
        return <Alert key={"success"} variant="success">
            The device was created successfully!
        </Alert>
    }

    const errorMessage = () => {
        return <Alert key={"error"} variant="error">
            Ups!! something is wrong.
        </Alert>
    }

    useEffect(() => {
        if (saved) {
            successMessage();
        } else {
            errorMessage();
        }
    }, [saved])


    const drawOptions = (data) => {
        let options = [];
        data.forEach((option) => {
            options.push(<option key={option.value} value={option.value}>{option.text}</option>);
        })
        return options;
    };


    const handleClose = () => setShow(false);

    return <Modal show={show} onHide={handleClose}>
        <Modal.Header closeButton>
            <Modal.Title>Add Device</Modal.Title>
        </Modal.Header>
        <Modal.Body>
            <Form noValidate validated={validated} onSubmit={handleSubmit}>
                <Form.Group className="mb-3" controlId="formBasicSystemName">
                    <Form.Label>System name *</Form.Label>
                    <Form.Control
                        type="text"
                        placeholder="Enter system name"
                        required
                        id="system_name"
                        value={entity?.system_name}
                        onKeyPress={(event) => {
                            if (event.target.value.length > 40) {
                                event.preventDefault();
                            }
                        }}
                    />
                    <Form.Control.Feedback
                        type="invalid">
                        {"System name is required"}
                    </Form.Control.Feedback>
                </Form.Group>

                <Form.Group className="mb-3" controlId="formBasicType">
                    <Form.Label>Type *</Form.Label>
                    <Form.Control
                        as="select"
                        id="type"
                        value={entity?.type}
                    >
                        {drawOptions(DEVICE_TYPE)}
                    </Form.Control>
                </Form.Group>
                <Form.Group className="mb-3" controlId="formBasicCapacity">
                    <Form.Label>HDD Capacity (GB) *</Form.Label>
                    <Form.Control
                        type="text"
                        placeholder="Enter an HDD Capacity"
                        id="hdd_capacity"
                        value={entity?.hdd_capacity}
                        required
                        onKeyPress={(event) => {
                            if (!/[0-9]/.test(event.key)) {
                                event.preventDefault();
                            }
                        }}/>
                    <Form.Control.Feedback type="invalid">
                        {"HDD Capacity is required"}
                    </Form.Control.Feedback>
                </Form.Group>
                <Button variant="primary" type="submit">
                    Save
                </Button>
            </Form>
        </Modal.Body>
    </Modal>

}

CreateEditForm.propTypes = {
    entity:   PropTypes.shape({
        id: PropTypes.number,
        system_name: PropTypes.string,
        type: PropTypes.string,
        hdd_capacity: PropTypes.number,
    }),
}
CreateEditForm.defaultProps = {
    entity: {},
}

export default CreateEditForm;
