import React, {useMemo, useState} from "react";
import useDeviceList from "../hooks/useDeviceList";
import Constants from "../util/Constants";
import PropTypes from "prop-types";
import {Button, Table} from "react-bootstrap";
import CreateEditForm from "../pages/CreateEditForm";
import {execute} from "../services/deviceService";

const {COLUMNS, DELETE} = Constants;

const DevicesTable = (props) => {
    const {filterBy, sortBy} = props;
    let {devicesList} = useDeviceList();
    const [entityDeleted, setEntityDeleted] = useState();
    const [entity, setEntity] = useState({});
    const [showModal, setShowModal] = useState(false);

    const data = useMemo(() => {
        let newData = devicesList;
        if (filterBy && filterBy !== "All") {
            newData = devicesList.filter(device => device.type === filterBy);
        }
        return Object.keys(newData).map(
            (key) => {
                if (entityDeleted !== newData[key]) {
                    return newData[key]
                }else{
                    return undefined;
                }
            }
        );
    }, [devicesList, filterBy, sortBy, entityDeleted]);

    const createHeader = () => {
        let header = [];
        COLUMNS.forEach(({text}) => {
            header.push(<th>{text}</th>);
        })
        header.push(<th>{"Selected"}</th>);
        return header;
    };

    const createBody = () => {
        let table = [];
        data.forEach((entity, index) => {
            let children = [];
            if (entity) {
                const {system_name, type, hdd_capacity} = entity;
                children.push(<td>{system_name}</td>);
                children.push(<td>{type}</td>);
                children.push(<td>{hdd_capacity}</td>);
                children.push(<td><Button
                    onClick={e => onSelect(e, entity)}
                    >Edit </Button><Button
                    onClick={e => onDelete(e, entity)}
                > Delete </Button></td>);
                children.push(<td>
                    <button className="btn btn-lg" style="background-color:transparent;">
                        <i className="fa fa-pencil"></i> Edit
                    </button></td>);
                table.push(<tr>{children}</tr>);
            }
        });
        return table;
    };

    const onSelect = (event, entity) => {
        setShowModal(true);
        setEntity(() => entity);
    };
    const onDelete = (event, entity) => {
        execute({data: entity, method: DELETE});
        setEntityDeleted(entity);
    };

    return (<>
            <Table>
                <thead>
                <tr>
                    {createHeader()}
                </tr>
                </thead>
                <tbody>
                {createBody()}
                </tbody>
            </Table>
            <br/>
            {showModal ? <CreateEditForm entity={entity}/> : <></>}
        </>
    );
};

DevicesTable.propTypes = {
    filterBy: PropTypes.string,
    sortBy: PropTypes.string,
}

DevicesTable.defaultProps = {
    filterBy: undefined,
    sortBy: undefined,
}
export default DevicesTable;
