import React, {useState} from "react";
import {Button, Form} from "react-bootstrap";
import PropTypes from "prop-types";
import Constants from "../util/Constants";

const {DEVICE_TYPE} = Constants;

const CreateEditForm = (props) => {
    const [validated, setValidated] = useState(false);

    const handleSubmit = (event) => {
        const form = event.currentTarget;
        if (form.checkValidity() === false) {
            event.preventDefault();
            event.stopPropagation();
        }

        setValidated(true);
    };
    const drawOptions = (data) => {
        let options = [];
        data.forEach((option) => {
            options.push(<option key={option.value} value={option.value}>{option.text}</option>);
        })
        return options;
    };

    return <Form noValidate validated={validated} onSubmit={handleSubmit}>
        <Form.Group className="mb-3" controlId="formBasicSystemName">
            <Form.Label>System name *</Form.Label>
            <Form.Control
                type="text"
                placeholder="Enter system name"
                required
                feedback="System name is required"
                feedbackType="invalid"
            />
        </Form.Group>

        <Form.Group className="mb-3" controlId="formBasicType">
            <Form.Label>Type *</Form.Label>
            <Form.Control as="select">
                {drawOptions(DEVICE_TYPE)}
            </Form.Control>
        </Form.Group>
        <Form.Group className="mb-3" controlId="formBasicCapacity">
            <Form.Label>HDD Capacity (GB) *</Form.Label>
            <Form.Control type="text" placeholder="Enter an HDD Capacity" required/>
        </Form.Group>
        <Button variant="primary" type="submit">
            Submit
        </Button>
    </Form>

}

CreateEditForm.propTypes = {
    visible: PropTypes.bool,
}
CreateEditForm.defaultProps = {
    visible: false,
}

export default CreateEditForm;
