import DevicesTable from "../Components/DevicesTable";
import React, {useState} from "react";
import Constants from "../util/Constants";
import {Form} from "react-bootstrap";

const {DEVICE_TYPE, COLUMNS, LABELS} = Constants;


const Dashboard = () => {
    const [filterBy, setFilterBy] = useState(undefined);
    const [sortBy, setSortBy] = useState(undefined);

    const handleFilter = (e, {value}) => {
        console.log("filterBy==>", value);
        setFilterBy(value);
    };
    const handleSort = (e, {value}) => {
        setSortBy(value);
        console.log("sortBy==>", sortBy);
    };

    return (<>
            <p>

                <Form.Select aria-label={LABELS.filter_device}>
                    <option>Open this select menu</option>
                    {DEVICE_TYPE.forEach(option => <option value={option.value} onChange={handleFilter}>{option.text}</option>)}
                </Form.Select>
            </p>
            <p>
                <Form.Select aria-label={LABELS.sort_by}>
                    <option>Open this select menu</option>
                    {COLUMNS.forEach(option => <option value={option.value} onChange={handleSort}>{option.text}</option>)}
                </Form.Select>
            </p>
            <p>
                <DevicesTable
                    sortBy={sortBy}
                    filterBy={filterBy}
                />
            </p>
        </>
    )
};

export default Dashboard;
