import DevicesTable from "../Components/DevicesTable";
import React, {useState} from "react";
import Constants from "../util/Constants";
import {Form} from "react-bootstrap";

const {DEVICE_TYPE, COLUMNS, LABELS} = Constants;


const Dashboard = () => {
    const [filterBy, setFilterBy] = useState(undefined);
    const [sortBy, setSortBy] = useState(undefined);

    const handleFilter = (event) => {
        console.log("filterBy==>", event.target.value);
        setFilterBy(event.target.value);
    };
    const handleSort = (e, {value}) => {
        setSortBy(value);
        console.log("sortBy==>", sortBy);
    };

    const drawOptions = (data) => {
        let options = [];
        data.forEach((option) => {
            options.push(<option value={option.value}>{option.text}</option>);
        })
        return options;
    };

    return (<>
            <p>
                <Form.Label>{LABELS.filter_device} </Form.Label>
                <Form.Control  as="select" onChange={handleFilter}>
                    <option value={undefined}>All</option>
                    {drawOptions(DEVICE_TYPE)}
                </Form.Control>
            </p>
            <p>
                <Form.Label>{LABELS.sort_by} </Form.Label>
                <Form.Select>
                    <option>All</option>
                    {drawOptions(COLUMNS)}
                </Form.Select>
            </p>
            <p>
                <DevicesTable
                    sortBy={sortBy}
                    filterBy={filterBy}
                />
            </p>
        </>
    )
};

export default Dashboard;
