import React, {useState} from "react";
import {Button, Form} from "react-bootstrap";
import PropTypes from "prop-types";
import Constants from "../util/Constants";
import useDeviceForm from "../hooks/useDeviceForm";

const {DEVICE_TYPE} = Constants;

const CreateEditForm = (props) => {
    const [validated, setValidated] = useState(false);
    const {entity} = props;
    const {create}= useDeviceForm();

    const handleSubmit = (event) => {
        const form = event.currentTarget;
        if (form.checkValidity() === false) {
            event.preventDefault();
            event.stopPropagation();
        }
        setValidated(true);
        console.log("form==", form.elements['formBasicSystemName'].value);
        // create(form);
    };

    const drawOptions = (data) => {
        let options = [];
        data.forEach((option) => {
            options.push(<option key={option.value} value={option.value}>{option.text}</option>);
        })
        return options;
    };

    return <Form noValidate validated={validated} onSubmit={handleSubmit}>
        <Form.Group className="mb-3" >
            <Form.Label>System name *</Form.Label>
            <Form.Control
                type="text"
                placeholder="Enter system name"
                required
                controlId="systemName"
            />
            <Form.Control.Feedback type="invalid">
                {"System name is required"}
            </Form.Control.Feedback>
        </Form.Group>

        <Form.Group className="mb-3">
            <Form.Label>Type *</Form.Label>
            <Form.Control as="select" controlId="type">
                {drawOptions(DEVICE_TYPE)}
            </Form.Control>
        </Form.Group>
        <Form.Group className="mb-3" >
            <Form.Label>HDD Capacity (GB) *</Form.Label>
            <Form.Control
                type="text"
                placeholder="Enter an HDD Capacity"
                controlId="formBasicCapacity"
                required/>
            <Form.Control.Feedback type="invalid">
                {"HDD Capacity is required"}
            </Form.Control.Feedback>
        </Form.Group>
        <Button variant="primary" type="submit">
            Submit
        </Button>
    </Form>

}

CreateEditForm.propTypes = {
    visible: PropTypes.bool,
}
CreateEditForm.defaultProps = {
    visible: false,
}

export default CreateEditForm;
