import * as RestService from './restService';

const server = "http://localhost:3000/";
const save = (data, url) => new Promise((resolve, reject) => {
    const options = {
        url: `${server}${url}`,
        method: 'POST',
        data,
    };
    RestService.send(options)
        .then((response) => {
            resolve(response);
        })
        .catch((error) => {
            const errorResponse = error.response.data;
            reject(errorResponse);
        });
});

const update = (data, url, id) => new Promise((resolve, reject) => {
    const options = {
        url: `${server}${url}/${id}`,
        method: 'PUT',
        data,
    };
    RestService.send(options)
        .then((response) => {
            resolve(response);
        })
        .catch((error) => {
            const errorResponse = error.response.data;
            reject(errorResponse);
        });
});

const remove = (data, url, id) => new Promise((resolve, reject) => {
    const options = {
        url: `${server}${url}/${id}`,
        method: 'DELETE',
        data,
    };
    console.log("options===>", options);
    RestService.send(options)
        .then((response) => {
            resolve(response);
        })
        .catch((error) => {
            const errorResponse = error.response.data;
            reject(errorResponse);
        });
});

const get = (url) => new Promise((resolve, reject) => {
    const options = {
        url: `${server}${url}`,
        method: "GET",
    };
    RestService.send(options)
        .then((response) => {
            resolve(response);
        })
        .catch((error) => {
            const errorResponse = error?.response?.data;
            reject(errorResponse);
        });
});


export {save, get, remove, update};
