import DevicesTable from "../Components/DevicesTable";
import React, {useState} from "react";
import Constants from "../util/Constants";
import {Form, DropdownButton, Dropdown} from "react-bootstrap";

const {DEVICE_TYPE, COLUMNS, LABELS} = Constants;


const Dashboard = () => {
    const [filterBy, setFilterBy] = useState(undefined);
    const [sortBy, setSortBy] = useState(undefined);

    const handleFilter = (value) => {
        console.log("filterBy==>", value);
        setFilterBy(value);
    };
    const handleSort = (e, {value}) => {
        setSortBy(value);
        console.log("sortBy==>", sortBy);
    };

    const drawOptions = (data) => {
        let options = [];
        data.forEach((option) => {
            options.push(
                <Dropdown.Item
                    value={option.value}
                    onClick={handleFilter(option.value)}
                >
                    {option.text}
                </Dropdown.Item>
            );
        })
        return options;
    };

    return (<>
            <p>
                <Form.Label>{LABELS.filter_device} </Form.Label>
                <DropdownButton id="dropdown-item-button" title="Open this select menu">
                    <Dropdown.ItemText>Dropdown item text</Dropdown.ItemText>
                    {drawOptions(DEVICE_TYPE)}
                </DropdownButton>
            </p>
            <p>
                <Form.Label>{LABELS.sort_by} </Form.Label>
                <Form.Select onChange={handleSort}>
                    <option>Open this select menu</option>
                    {drawOptions(COLUMNS)}
                </Form.Select>
            </p>
            <p>
                <DevicesTable
                    sortBy={sortBy}
                    filterBy={filterBy}
                />
            </p>
        </>
    )
};

export default Dashboard;
