import React, {useMemo, useState} from "react";
import useDeviceList from "../hooks/useDeviceList";
import Constants from "../util/Constants";
import PropTypes from "prop-types";
import {Button, Table} from "react-bootstrap";
import * as Icons from "react-bootstrap-icons";
import {Link} from "react-bootstrap-icons";
import CreateEditForm from "../pages/CreateEditForm";


const {COLUMNS} = Constants;


const DevicesTable = (props) => {
    const {filterBy, sortBy} = props;
    let {devicesList, isLoading} = useDeviceList();
    const [edits, setEdits] = useState({});
    isLoading = false;

    const data = useMemo(() => {
        let newData = devicesList;
        if (filterBy && filterBy !== "All") {
            newData = devicesList.filter(device => device.type === filterBy);
            // devicesList.forEach(item => {
            //         filterBy.forEach(filter => {
            //             if (item.type === filter) {
            //                 newData.push(item);
            //             }
            //         });
            //     }
            // );
        }
        return Object.keys(newData).map(
            (key) => edits[key] || newData[key]
        );
    }, [devicesList, edits, filterBy, sortBy]);

    const createHeader = () => {
        let header = [];
        COLUMNS.forEach(({text}) => {
            header.push(<th>{text}</th>);
        })
        header.push(<th>{"Selected"}</th>);
        return header;
    };

    const createBody = () => {
        let table = [];
        data.forEach((entity) => {
            let children = [];
            const {system_name, type, hdd_capacity} = entity;
            children.push(<td>{system_name}</td>);
            children.push(<td>{type}</td>);
            children.push(<td>{hdd_capacity}</td>);
            children.push(<td><Button id={entity} onClick={onSelect} value={entity}>Edit</Button> </td>);
            table.push(<tr>{children}</tr>);
        });
        return table;
    };

    const onSelect = (event) => {
        console.log("hola", event.target.value, "===");
        return <CreateEditForm entity={event.target.value}/>
    };

    return (<Table>
        <thead>
        <tr>
            {createHeader()}
        </tr>
        </thead>
        <tbody>
        {createBody()}
        </tbody>
    </Table>);
};

DevicesTable.propTypes = {
    filterBy: PropTypes.string,
    sortBy: PropTypes.string,
}

DevicesTable.defaultProps = {
    filterBy: undefined,
    sortBy: undefined,
}
export default DevicesTable;
